custom_css:
html:lang(ar-SA) {
	direction: rtl;
    
    .header-bar {
        right: 0;
        left: auto;
    }
    
    .header-bar .title-container .headline{
         left: auto;
    }
    
    .title-container {
        margin-right: 16px;
        margin-left: 0;
    }
    
    .actions .action-buttons[data-v-7d77de31] > :not(:last-child) {
    	margin-left: 8px;
        margin-right: 0px;
    }
    
    .resize-wrapper .grab-bar {
        right: auto;
        left: 0;
    }
    
    @media only screen and (max-width: 960px){
        .private-view #navigation {
            right: 0;
            transform: translate(100%);
            border-left: var(--theme--navigation--border-width) solid var(--theme--navigation--border-color);
            left: auto;
        }
        
        .title-container {
            margin-right: 16px;
            margin-left: 0px;
        }
    }
}

.bookmark-controls, #sidebar .sidebar-detail-group .sidebar-detail:not(:first-child) {
	display: none;
}
